import React, { useState } from "react";
import axios from "axios";
import "../App.css";

const SaleRequest = ({ products, fetchProducts }) => {
  const [productId, setProductId] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [discount, setDiscount] = useState(10);
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const API_BASE = process.env.REACT_APP_API_BASE_URL;

  const handlePurchase = async () => {
    if (!productId || quantity < 1) {
      setMessage("âš ï¸ Please select a product and valid quantity.");
      return;
    }
    try {
      setLoading(true);
      const res = await axios.post(`${API_BASE}/purchase`, {
        productId,
        quantity: Number(quantity),
        discount: Number(discount),
      });
      setMessage(`âœ… Final Price: â‚¹${res.data.finalPrice}`);
      fetchProducts();
    } catch (err) {
      setMessage(`âŒ ${err.response?.data?.error || "Purchase failed"}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="sale-form">
      <h2>ðŸ›’ Make a Sale</h2>

      <div className="form-group">
        <label>Product:</label>
        <select
          value={productId}
          onChange={(e) => setProductId(e.target.value)}
        >
          <option value="">--Select--</option>
          {products.map((p) => (
            <option key={p.id} value={p.id}>
              {p.name}
            </option>
          ))}
        </select>
      </div>

      <div className="form-group">
        <label>Quantity:</label>
        <input
          type="number"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
          min="1"
        />
      </div>

      <div className="form-group">
        <label>Discount (%):</label>
        <input
          type="number"
          value={discount}
          onChange={(e) => setDiscount(e.target.value)}
          min="0"
          max="100"
        />
      </div>

      <button onClick={handlePurchase} disabled={loading}>
        {loading ? "Processing..." : "Checkout"}
      </button>

      {message && <p className="message">{message}</p>}
    </div>
  );
};

export default SaleRequest;
